const BrainMap = React.memo(({ }) => {
    // Track animation time for dynamic color cycling.
    const [animationTime, setAnimationTime] = useState(0);
    // Store which regions have been clicked (i.e. are in warm mode).
    const [selectedRegions, setSelectedRegions] = useState({});

    // Animation loop for updating animationTime.
    useEffect(() => {
        let animationFrameId;
        let lastTimestamp = 0;
        const fps = 30; // Limit to 30 FPS
        const frameInterval = 1000 / fps;

        const updateTime = (timestamp) => {
            if (timestamp - lastTimestamp >= frameInterval) {
                setAnimationTime(timestamp);
                lastTimestamp = timestamp;
            }
            animationFrameId = requestAnimationFrame(updateTime);
        };

        animationFrameId = requestAnimationFrame(updateTime);
        return () => cancelAnimationFrame(animationFrameId);
    }, []);

    // 5-second cycle for the color oscillation.
    const period = 5000;
    // Global phase (in radians) based on the current animation time.
    const phase = (animationTime % period) * ((2 * Math.PI) / period);

    // Toggle the selected (warm) state for a region.
    const handleRegionClick = (regionId) => {
        setSelectedRegions((prev) => ({
            ...prev,
            [regionId]: !prev[regionId],
        }));
    };

    const regionStyle = { transition: "fill 0.5s ease" };

    return (
        <div className="container">
            <svg
                width="100%"
                height="100%"
                viewBox="0 90 200 120"
                preserveAspectRatio="xMidYMid meet"
            >
                {brainRegionsBase.map((region, index) => {
                    const regionPhase = phase + index * 0.3;
                    const isWarm = !!selectedRegions[region.id];
                    const effectiveHue = isWarm
                        ? 30 + 30 * Math.sin(regionPhase - Math.PI / 2)
                        : 240 + 60 * Math.sin(regionPhase - Math.PI / 2);
                    const rgb = hslToRgb(effectiveHue, 1, 0.5);
                    const fillColor = `rgb(${rgb.r}, ${rgb.g}, ${rgb.b})`;

                    return (
                        <g
                            key={region.id}
                            className="region-wrapper"
                            onClick={() => handleRegionClick(region.id)}
                        >
                            <path
                                id={region.id}
                                d={region.path}
                                fill={fillColor}
                                className="region"
                                data-tooltip={region.name}
                                style={regionStyle}
                                onMouseEnter={(e) => {
                                    e.target.style.stroke = "#333";
                                    e.target.style.strokeWidth = "1";
                                    e.target.parentNode.appendChild(e.target);
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.stroke = "none";
                                }}
                            />
                        </g>
                    );
                })}
            </svg>
        </div>
    );
});

export default BrainMap;
